#include <cstdlib>
#include <iostream>
#include <string>
#include <fstream>
#include <sstream>
#include <limits>


int main(int argc, char** argv)
{
	int day = 1;
	if (argc >= 2)
		day = atoi(argv[1]);

	switch (day)
	{
		case 1:
		{
			const char* seq = "";

			// part a
			const char* iter = seq;
			int suma = 0;
			size_t n = 1;
			while (*++iter)
			{
				if (*iter == *(iter-1))
					suma += *iter - '0';
				++n; // count for length of string (easier for b)
			}

			// do not forget wrapping
			if (*seq == *(iter-1))
				suma += *seq - '0';

			// part b
			int sumb = 0;
			int p = n / 2;
			for (size_t i = 0; i < n; ++i)
			{
				if (seq[i] == seq[(i+p) % n])
					sumb += seq[i] - '0';
			}

			std::cout << "1a: " << suma
				<< "      1b: " << sumb << std::endl;
		}

		case 2:
		{
			std::ifstream infile("input/input02.dat");
			std::string line;
			int suma = 0;
			int sumb = 0;
			while (std::getline(infile, line))
			{
				std::istringstream iss(line);
				int n = 0;
				int tmp;
				int min = std::numeric_limits<int>::max();
				int max = std::numeric_limits<int>::min();
				while (iss >> tmp)
				{
					++n;

					// part a
					if (tmp > max)
						max = tmp;
					if (tmp < min)
						min = tmp;

					// part b
					std::istringstream iss2(line);
					int m = 0;
					int tmp2;
					while (iss2 >> tmp2)
					{
						++m;
						if (m == n) continue;

						if (tmp%tmp2 == 0)
						{
							sumb += tmp / tmp2;
							std::cout << "adding " << tmp << " / " << tmp2 << std::endl;
						}
					}
				}
				suma += max - min;
			}

			std::cout << "2a: " << suma
				<< "      1b: " << sumb << std::endl;
		}
	}

	return 0;
}

